import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useUser } from '../UserState';

export const Friends = () => {
    const [user] = useUser();
    const [userId, setUserId] = useState('');
    const [friendEmail, setFriendEmail] = useState('');
    const [friends, setFriends] = useState([]);
    const [requests, setRequests] = useState({ received: [], sent: [] });

    // Define the base URL for the backend
    const backendBaseUrl = 'http://localhost:8082';

    useEffect(() => {
        // This effect should run when 'user' changes, which would happen after login.
        if (user.isAuthenticated && user.id) {
            setUserId(user.id); // Assuming 'user.id' is where the user ID is stored
        }
    }, [user]); // The effect runs when 'user' changes

    useEffect(() => {
        if (!userId) {
            console.error('UserId not set');
            return;
        }
    }, [userId]);

    // Fetch friends and friend requests on component mount
    useEffect(() => {
        const fetchFriends = async () => {
            try {
                const res = await axios.get(`${backendBaseUrl}/api/friends/list/${userId}`);
                setFriends(res.data);
            } catch (err) {
                console.error('Error fetching friends:', err);
            }
        };

        const fetchRequests = async () => {
            try {
                const response = await axios.get(`${backendBaseUrl}/api/friends/pending/${userId}`);
                setRequests(prevRequests => ({ ...prevRequests, received: response.data }));
            } catch (error) {
                console.error('Error fetching pending friend requests:', error);
            }
        };

        fetchFriends();
        fetchRequests();
    }, [userId]);

    const handleAddFriend = async () => {
        try {
            await axios.post(`${backendBaseUrl}/api/friends/request`, { requesterId: userId, recipientEmail: friendEmail });
            alert('Friend request sent!');
        } catch (err) {
            const errorMessage = err.response?.data?.message || err.message;
            alert(`Failed to send friend request: ${errorMessage}`);
        }
    };

    const handleResponse = async (requestId, status) => {
        try {
            await axios.put(`${backendBaseUrl}/api/friends/response`, { friendId: requestId, status });
            alert(`Friend request ${status}!`);
            
            // Update local state to remove the handled request from the list
            setRequests(prevRequests => ({
                ...prevRequests,
                received: prevRequests.received.filter(request => request._id !== requestId)
            }));
        } catch (error) {
            alert(`Error updating friend request: ${error.message}`);
        }
    };

    return (
        <div className="friends-page">
            <h1>Friends</h1>
            <input
                value={friendEmail}
                onChange={(e) => setFriendEmail(e.target.value)}
                placeholder="Enter friend's email"
            />
            <button onClick={handleAddFriend}>Add Friend</button>
            <h2>My Friends</h2>

            <h1>Friends List</h1>
            {user.isAuthenticated ? (
                <p>Showing friends for {user.username}</p>
            ) : (
                <p>Please log in to see your friends.</p>
            )}
            <ul>
                {friends.map(friend => <li key={friend._id}>{friend.username}</li>)}
            </ul>
            <h2>Friend Requests</h2>
            <ul>
                {requests.received.map(request => (
                    <li key={request._id}>
                        {request.requester.username} {/* Adjusted to username if that's the populated field */}
                        <button onClick={() => handleResponse(request._id, 'accepted')}>Accept</button>
                        <button onClick={() => handleResponse(request._id, 'declined')}>Decline</button>
                    </li>
                ))}
            </ul>
        </div>
    );
};
