import React, { useState, useEffect } from 'react';
import axios from 'axios';

export const Friends = () => {
    const [userId, setUserId] = useState('6624cebca391867a21a0d716'); // You might get this from context or props
    const [friendEmail, setFriendEmail] = useState('');
    const [friends, setFriends] = useState([]);
    const [requests, setRequests] = useState({ received: [], sent: [] });

    // Define the base URL for the backend
    const backendBaseUrl = 'http://localhost:8082';

    useEffect(() => {
        if (!userId) {
            // Maybe set an error or just return early.
            console.error('UserId not set');
            return;
        }
    
        // Rest of your useEffect that makes the API call
    }, [userId]); // This will re-run when userId changes

    // Fetch friends and friend requests on component mount
    useEffect(() => {
        const fetchFriends = async () => {
            try {
                const res = await axios.get(`${backendBaseUrl}/api/friends/list/${userId}`);
                setFriends(res.data);
            } catch (err) {
                console.error('Error fetching friends:', err);
            }
        };

        const fetchRequests = async () => {
            // Placeholder for API endpoint to fetch friend requests
            // Example: axios.get(`${backendBaseUrl}/api/friends/requests/${userId}`);
        };

        fetchFriends();
        fetchRequests();
    }, [userId]);

    //fetch friend request
    useEffect(() => {
        const fetchPendingRequests = async () => {
            try {
                const response = await axios.get(`${backendBaseUrl}/api/friends/pending/${userId}`);
                setRequests({ ...requests, received: response.data });
            } catch (error) {
                console.error('Error fetching pending friend requests:', error);
            }
        };
    
        if (userId) {
            fetchPendingRequests();
        }
    }, [userId]);

    const handleAddFriend = async () => {
        try {
            await axios.post(`${backendBaseUrl}/api/friends/request`, { requesterId: userId, recipientEmail: friendEmail });
            alert('Friend request sent!');
        } catch (err) {
            // If the response is available, use the response message otherwise use the error message
            const errorMessage = err.response?.data?.message || err.message;
            alert(`Failed to send friend request: ${errorMessage}`);
        }
    };

    return (
        <div className="friends-page">
            <h1>Friends</h1>
            <input
                value={friendEmail}
                onChange={(e) => setFriendEmail(e.target.value)}
                placeholder="Enter friend's email"
            />
            <button onClick={handleAddFriend}>Add Friend</button>
            <h2>My Friends</h2>
            <ul>
                {friends.map(friend => <li key={friend._id}>{friend.name}</li>)}
            </ul>
            {/* Implement lists for friend requests here */}
        </div>
    );
};
